function userStatusUpdate(s,a,t){axios.post(s,{status:$("#status_id_"+a).val()}).then(s=>{s.data.message?toastr.success(s.data.message,"Success"):toastr.success("User Status Successfully Updated.")}).catch(s=>{$("#status_id_"+a).val(t),s.response.data.message?toastr.error(s.response.data.message,"Error"):toastr.error("Failed to Update User Status")})}function userMembershipStatusUpdate(s,a,t,e){axios.post(s,{membership:$("#mem_status_id_"+a).val()}).then(s=>{var a=$(e);a.DataTable().ajax.reload(),s.data.message?toastr.success(s.data.message,"Success"):toastr.success("User Status Successfully Updated.")}).catch(s=>{$("#mem_status_id_"+a).val(t),s.response.data.message?toastr.error(s.response.data.message,"Error"):toastr.error("Failed to Update User Status")})}