function userStatusUpdate(s,t,a){axios.post(s,{status:$("#status_id_"+t).val()}).then(s=>{s.data.message?toastr.success(s.data.message,"Success"):toastr.success("User Status Successfully Updated.")}).catch(s=>{$("#status_id_"+t).val(a),s.response.data.message?toastr.error(s.response.data.message,"Error"):toastr.error("Failed to Update User Status")})}function userMembershipStatusUpdate(s,t,a){axios.post(s,{membership:$("#mem_status_id_"+t).val()}).then(s=>{s.data.message?toastr.success(s.data.message,"Success"):toastr.success("User Status Successfully Updated.")}).catch(s=>{$("#mem_status_id_"+t).val(a),s.response.data.message?toastr.error(s.response.data.message,"Error"):toastr.error("Failed to Update User Status")})}